  qry = toolbox.executeQuery(
      ''UPDATE quhead SET quhead_billtocountry = <? value("newCountry") ?>''
    + '' WHERE quhead_billtocountry = <? value("oldCountry") ?>''
    + ''  <? if not exists("showAll") ?>''
