  qry = toolbox.executeQuery(
      ''UPDATE quhead SET quhead_shiptocountry = <? value("newCountry") ?>''
    + '' WHERE quhead_shiptocountry = <? value("oldCountry") ?>''
    + ''  <? if not exists("showAll") ?>''
