INSERT INTO pkgreport (report_id, report_name, report_sys, report_source, report_descrip, report_grade, report_loaddate) VALUES (441, 'TimeExpenseSheet', NULL, '<!DOCTYPE openRPTDef>
<report>
 <title>Time Expense Sheet</title>
 <name>TimeExpenseSheet</name>
 <description></description>
 <parameter default="2000" active="true" listtype="dynamic" type="string" name="tehead_id">
  <description></description>
  <query>SELECT tehead_id, tehead_number || ''-'' || tehead_username FROM tehead ORDER By tehead_number DESC;</query>
 </parameter>
 <grid>
  <snap/>
  <show/>
  <x>0.05</x>
  <y>0.05</y>
 </grid>
 <size>Letter</size>
 <portrait/>
 <topmargin>50</topmargin>
 <bottommargin>50</bottommargin>
 <rightmargin>50</rightmargin>
 <leftmargin>50</leftmargin>
 <querysource>
  <name>header</name>
  <sql>SELECT 
  tehead_number,
  formatDate(tehead_weekending) AS f_weekending,
  warehous_code,
  emp_code || '' - '' || COALESCE(cntct_name,'''') AS f_employee,
  CASE WHEN (tehead_status=''O'') THEN 
    &lt;? value("open") ?> 
  WHEN (tehead_status=''A'') THEN
    &lt;? value("approved") ?> 
  ELSE &lt;? value("closed") ?>
  END AS f_status,
  tehead_notes
FROM te.tehead
 JOIN emp ON (tehead_emp_id=emp_id)
 JOIN whsinfo ON (warehous_id=tehead_warehous_id)
 LEFT OUTER JOIN cntct ON (emp_cntct_id=cntct_id)
WHERE (tehead_id=&lt;? value("tehead_id") ?>);
</sql>
 </querysource>
 <querysource>
  <name>time</name>
  <sql>SELECT
  teitem_tehead_id,
  formatDate(teitem_workdate) AS f_workdate,
  prj_number,
  prj_name,
  prjtask_number,
  prjtask_name,
  cust_number,
  cust_name,
  item_number,
  item_descrip1,
  formatQty(teitem_qty) AS f_hours,
  teitem_qty,
  CASE WHEN (teitem_billable) THEN
    formatMoney(teitem_total)
  ELSE '' -- '' END AS f_billing_total,
  CASE WHEN (teitem_billable) THEN
    teitem_total
  ELSE 0 END AS billing_total,
  formatBoolYN(teitem_billable) AS f_billable,
  teitem_notes
FROM te.teitem
  JOIN prjtask ON (teitem_prjtask_id=prjtask_id)
  JOIN prj ON (prjtask_prj_id=prj_id)
  JOIN item ON (item_id=teitem_item_id)
  LEFT OUTER JOIN custinfo ON (cust_id=teitem_cust_id)
WHERE ((teitem_tehead_id=&lt;? value("tehead_id") ?>)
  AND (teitem_type=''T''))
ORDER BY teitem_tehead_id, teitem_workdate, teitem_linenumber;</sql>
 </querysource>
 <querysource>
  <name>expense</name>
  <sql>SELECT
  teitem_tehead_id,
  formatDate(teitem_workdate) AS f_workdate,
  prj_number,
  prj_name,
  prjtask_number,
  prjtask_name,
  cust_number,
  cust_name,
  item_number,
  item_descrip1,
  formatQty(teitem_qty) AS f_qty,
  teitem_qty,
  formatMoney(teitem_total) AS f_total,
  teitem_total,
  CASE WHEN (teitem_billable) THEN
    formatMoney(teitem_total)
  ELSE '' -- '' END AS f_billing_total,
  CASE WHEN (teitem_type = ''E'') THEN
    teitem_total
  ELSE 0 END AS expense_total,
  CASE WHEN (teitem_billable) THEN
    teitem_total
  ELSE 0 END AS billing_total,
  formatBoolYN(teitem_billable) AS f_billable,
  teitem_notes
FROM te.teitem
  JOIN prjtask ON (teitem_prjtask_id=prjtask_id)
  JOIN prj ON (prjtask_prj_id=prj_id)
  JOIN item ON (item_id=teitem_item_id)
  LEFT OUTER JOIN custinfo ON (cust_id=teitem_cust_id)
WHERE ((teitem_tehead_id=&lt;? value("tehead_id") ?>)
  AND (teitem_type=''E''))
ORDER BY teitem_workdate, teitem_linenumber;</sql>
 </querysource>
 <querysource>
  <name>prjsum</name>
  <sql>SELECT
  teitem_tehead_id,
  prj_number,
  SUM(teitem_qty) AS total_hours,
  SUM(teitem_total) AS billing_total,
CASE WHEN (
            (checkprivilege(''MaintainEmpCostAll'') 
              OR 
              (checkprivilege(''MaintainEmpCostSelf'') 
               AND 
               current_user = (SELECT tehead_username 
                              FROM te.tehead 
                              WHERE teitem_tehead_id = tehead_id)
              ) 
            )
          )
           THEN SUM(teitem_empcost * teitem_qty)
           ELSE 0 END AS cost_total,
CASE WHEN (
            (checkprivilege(''MaintainEmpCostAll'') 
              OR
              (checkprivilege(''MaintainEmpCostSelf'')
               AND 
               current_user = (SELECT tehead_username 
                               FROM te.tehead 
                               WHERE teitem_tehead_id = tehead_id)
              )
            )
           )
           THEN ''Cost''
           ELSE ''No Priv'' END AS title

FROM te.teitem
  JOIN prjtask ON (teitem_prjtask_id=prjtask_id)
  JOIN prj ON (prjtask_prj_id=prj_id)
  JOIN item ON (item_id=teitem_item_id)
  LEFT OUTER JOIN custinfo ON (cust_id=teitem_cust_id)
WHERE ((teitem_tehead_id=&lt;? value("tehead_id") ?>)
  AND (teitem_type=''T''))
GROUP BY teitem_tehead_id, prj_number;</sql>
 </querysource>
