  var params = new Object;
  if (_teitemid > 0)
    params.teitem_id = _teitemid;
  else
    params.tehead_id = _headid;

  var q = toolbox.executeDbQuery("timeexpensesheetitem", "teitemprev", params);

  if (q.first())
