  else
  {
    var params = new Object;
    params.teitem_id = _teitemid;
    var q = toolbox.executeDbQuery("timeexpensesheetitem","teitemnext",params);

    if (q.first())
    {
      _modified = false;
      _teitemid = q.value("teitem_id");
      _prev.enabled = true;
      xtte.timeExpenseSheetItem.populate();
