INSERT INTO pkgmetasql (metasql_id, metasql_group, metasql_name, metasql_notes, metasql_query, metasql_lastuser, metasql_lastupdate, metasql_grade) VALUES (213, 'desktop', 'mfgAct', ' Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple. See www.xtuple.com/CPAL for the full text of the software license.', '-- Group: desktop
-- Name:  mfgAct
-- Notes: 
-- Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple.
-- See www.xtuple.com/CPAL for the full text of the software license.

<? if exists("planned") ?> 
SELECT ''L'' AS activity, <? value("planned") ?> AS activity_qtdisplayrole, 0 AS seq,
  COUNT(planord_id) AS count, COALESCE(sum(stdcost(item_id) * planord_qty),0) AS amount, 
  sum(planord_qty) AS qty,
  ''curr'' AS amount_xtnumericrole, ''qty'' AS qty_xtnumericrole
FROM planord
  JOIN itemsite ON (planord_itemsite_id = itemsite_id)
  JOIN item ON (itemsite_item_id = item_id)
WHERE (planord_type = ''W'')
HAVING (COUNT(planord_id) > 0)
UNION
<? endif ?>
SELECT ''O'' AS activity, <? value("open") ?> AS activity_qtdisplayrole, 4 AS seq,   
  COUNT(wo_id) AS count, 
  SUM(round((wo_qtyord - wo_qtyrcv) * stdcost(itemsite_item_id),2)) AS amount, 
  SUM(wo_qtyord - wo_qtyrcv) AS qty,
  ''curr'' AS amount_xtnumericrole, ''qty'' AS qty_xtnumericrole
FROM wo
  JOIN itemsite ON (wo_itemsite_id=itemsite_id)
WHERE (wo_status = ''O'')
HAVING (COUNT(wo_id) > 0)
UNION ALL
SELECT ''E'' AS activity, <? value("exploded") ?> AS activity_qtdisplayrole, 4 AS seq,   
  COUNT(wo_id) AS count, 
  SUM(round((wo_qtyord - wo_qtyrcv) * stdcost(itemsite_item_id),2)) AS amount, 
  SUM(wo_qtyord - wo_qtyrcv) AS qty,
  ''curr'' AS amount_xtnumericrole, ''qty'' AS qty_xtnumericrole
FROM wo
  JOIN itemsite ON (wo_itemsite_id=itemsite_id)
WHERE (wo_status = ''E'')
HAVING (COUNT(wo_id) > 0)
UNION ALL
SELECT ''R'' AS activity, <? value("released") ?> AS activity_qtdisplayrole, 4 AS seq,   
  COUNT(wo_id) AS count, 
  SUM(round((wo_qtyord - wo_qtyrcv) * stdcost(itemsite_item_id),2)) AS amount, 
  SUM(wo_qtyord - wo_qtyrcv) AS qty,
  ''curr'' AS amount_xtnumericrole, ''qty'' AS qty_xtnumericrole
FROM wo
  JOIN itemsite ON (wo_itemsite_id=itemsite_id)
WHERE (wo_status = ''R'')
HAVING (COUNT(wo_id) > 0)
UNION ALL
SELECT ''I'' AS activity, <? value("inprocess") ?> AS activity_qtdisplayrole, 4 AS seq,   
  COUNT(wo_id) AS count, 
  SUM(round((wo_qtyord - wo_qtyrcv) * stdcost(itemsite_item_id),2)) AS amount, 
  SUM(wo_qtyord - wo_qtyrcv) AS qty,
  ''curr'' AS amount_xtnumericrole, ''qty'' AS qty_xtnumericrole
FROM wo
  JOIN itemsite ON (wo_itemsite_id=itemsite_id)
WHERE (wo_status = ''I'')
HAVING (COUNT(wo_id) > 0)
ORDER BY seq;
', NULL, NULL, 0);
INSERT INTO pkgmetasql (metasql_id, metasql_group, metasql_name, metasql_notes, metasql_query, metasql_lastuser, metasql_lastupdate, metasql_grade) VALUES (215, 'desktop', 'mfgOpen', ' Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple. See www.xtuple.com/CPAL for the full text of the software license.', '-- Group: desktop
-- Name:  mfgOpen
-- Notes: 
-- Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple.
-- See www.xtuple.com/CPAL for the full text of the software license.

SELECT wo_id,
       CASE WHEN (wo_ordid IS NULL) THEN -1
            ELSE wo_ordid
       END AS orderid, wo_status,
       CASE WHEN (wo_status = ''E'') THEN <? value("exploded") ?>
         WHEN (wo_status = ''I'') THEN <? value("inprocess") ?>
         WHEN (wo_status = ''R'') THEN <? value("released") ?>
         WHEN (wo_status = ''O'') THEN <? value("open") ?>
       END AS wo_status_qtdisplayrole, 
       warehous_code, uom_name,
       item_number, (item_descrip1 || '' '' || item_descrip2) AS itemdescrip,
       wo_qtyord, wo_qtyrcv, wo_startdate, wo_duedate,
       wo_number::TEXT || ''-'' || wo_subnumber::TEXT AS wonumber,
       wo_postedvalue, wo_wipvalue,
       ''qty'' AS wo_qtyord_xtnumericrole,
       ''qty'' AS wo_qtyrcv_xtnumericrole,
       ''curr'' AS wo_postedvalue_xtnumericrole,
       ''curr'' AS wo_wipvalue_xtnumericrole,
       CASE WHEN ((wo_startdate<=CURRENT_DATE) AND (wo_status IN (''O'',''E'',''S'',''R''))) THEN ''error''
       END AS wo_startdate_qtforegroundrole,
       CASE WHEN (wo_duedate<=CURRENT_DATE) THEN ''error''
       END AS wo_duedate_qtforegroundrole,
       CASE WHEN (wo_duedate<=CURRENT_DATE) THEN ''Overdue''
            ELSE ''On Time''
       END AS condition,
       CASE WHEN (wo_duedate<=CURRENT_DATE) THEN ''error'' END AS condition_qtforegroundrole
FROM wo
  JOIN itemsite ON (wo_itemsite_id=itemsite_id)
  JOIN whsinfo ON (itemsite_warehous_id=warehous_id)
  JOIN item ON (itemsite_item_id=item_id)
  JOIN uom ON (item_inv_uom_id=uom_id)
WHERE (wo_status != ''C'')
ORDER BY wo_number, wo_subnumber;
', NULL, NULL, 0);
