INSERT INTO pkgmetasql (metasql_id, metasql_group, metasql_name, metasql_notes, metasql_query, metasql_lastuser, metasql_lastupdate, metasql_grade) VALUES (219, 'desktop', 'purchHist', 'Summarized Purchase History for Desktop Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple. See www.xtuple.com/CPAL for the full text of the software license.', '-- Group: desktop
-- Name:  purchHist
-- Notes: Summarized Purchase History for Desktop
--        Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple.
--        See www.xtuple.com/CPAL for the full text of the software license.

--------- VARIANCES -----------
<? if exists("variances") ?> 
SELECT <? literal("group_id") ?>, <? literal("group_number") ?>,  <? literal("group_name") ?>,
  sum(qty) AS qty, sum(amount) AS amount,
  ''curr'' AS amount_xtnumericrole,
  ''qty'' AS qty_xtnumericrole,
   0 AS amount_xttotalrole,
   0 AS qty_xttotalrole
FROM (
SELECT vend_id, vend_number, vend_name,
       pohead_agent_username, usr_propername,
       COALESCE(item_id,-2) AS item_id,
       COALESCE(item_number,<? value("nonInv") ?>) AS item_number,
       item_descrip1,
       recv_qty AS qty, 
       currToBase(vohead_curr_id, sum(vodist_amount) / sum(vodist_qty), vohead_distdate) * recv_qty - recv_value AS amount
FROM recv
  JOIN vendinfo ON (recv_vend_id=vend_id)
  LEFT OUTER JOIN itemsite ON (recv_itemsite_id=itemsite_id)
  LEFT OUTER JOIN item     ON (itemsite_item_id=item_id)
  JOIN vodist ON ((vodist_poitem_id=recv_orderitem_id) 
              AND (vodist_vohead_id=recv_vohead_id))
  JOIN vohead ON ((vodist_vohead_id=vohead_id) 
              AND (vohead_posted))
  JOIN poitem ON (recv_orderitem_id=poitem_id)
  JOIN pohead ON (poitem_pohead_id=pohead_id)
  LEFT OUTER JOIN usr ON (pohead_agent_username=usr_username)
WHERE (vohead_distdate BETWEEN <? value("startDate") ?> AND <? value("endDate") ?>
   AND (recv_order_type=''PO''))
GROUP BY recv_qty, recv_value, vohead_curr_id, vohead_distdate,
  vend_id, vend_number, vend_name,
  pohead_agent_username, usr_propername,
  item_id, item_number, item_descrip1
) data
WHERE (amount > 0)
GROUP BY <? literal("group_id") ?>, <? literal("group_number") ?>, <? literal("group_name") ?>

--------- RECEIPTS -------
<? elseif exists("receipts") ?> 
SELECT <? literal("group_id") ?>, <? literal("group_number") ?>,  <? literal("group_name") ?>,
       sum(amount) as amount, sum(qty) AS qty,
       ''curr'' AS amount_xtnumericrole,
       ''qty'' AS qty_xtnumericrole,
       0 AS amount_xttotalrole,
       0 AS qty_xttotalrole
FROM (
  SELECT vend_id, vend_number, vend_name,
         pohead_agent_username, usr_propername,
         COALESCE(item_id,-2) AS item_id, COALESCE(item_number,<? value("nonInv") ?>) AS item_number,
         item_descrip1,
         coalesce(recv_value,0) AS amount,
         recv_qty AS qty
  FROM recv
    JOIN vendinfo ON (vend_id=recv_vend_id)
    LEFT OUTER JOIN itemsite ON (recv_itemsite_id=itemsite_id)
    LEFT OUTER JOIN item     ON (itemsite_item_id=item_id)
    JOIN poitem ON (recv_orderitem_id=poitem_id)
    JOIN pohead ON (poitem_pohead_id=pohead_id)
    LEFT OUTER JOIN usr ON (pohead_agent_username=usr_username)
  WHERE ((recv_date::date BETWEEN <? value("startDate") ?> AND <? value("endDate") ?>)
    AND (recv_posted)
    AND (recv_order_type = ''PO''))
  UNION ALL
  SELECT vend_id, vend_number, vend_name,
         pohead_agent_username, usr_propername,
         COALESCE(item_id,-1) AS item_id, COALESCE(item_number,<? value("nonInv") ?>) AS item_number,
         item_descrip1,
         coalesce(poreject_value,0) * -1 AS amount,
         poreject_qty * -1 AS qty
  FROM poreject
    JOIN vendinfo ON (vend_id=poreject_vend_id)
    JOIN itemsite ON (itemsite_id=poreject_itemsite_id)
    JOIN item ON (item_id=itemsite_item_id)
    JOIN poitem ON (poreject_poitem_id=poitem_id)
    JOIN pohead ON (poitem_pohead_id=pohead_id)
    LEFT OUTER JOIN usr ON (pohead_agent_username=usr_username)
  WHERE ((poreject_date::date BETWEEN <? value("startDate") ?> AND <? value("endDate") ?>)
    AND (poreject_posted))
) data
GROUP BY <? literal("group_id") ?>, <? literal("group_number") ?>, <? literal("group_name") ?>
ORDER BY <? literal("group_number") ?>
;
<? endif ?>
', NULL, NULL, 0);
INSERT INTO pkgmetasql (metasql_id, metasql_group, metasql_name, metasql_notes, metasql_query, metasql_lastuser, metasql_lastupdate, metasql_grade) VALUES (220, 'desktop', 'purchOpen', ' Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple. See www.xtuple.com/CPAL for the full text of the software license.', '-- Group: desktop
-- Name:  purchOpen
-- Notes: 
-- Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple.
-- See www.xtuple.com/CPAL for the full text of the software license.

SELECT pohead_id, pohead_number, vend_number, vend_name, pohead_shipvia, 
  formatCntctName(pohead_vend_cntct_honorific, 
                  pohead_vend_cntct_first_name, 
                  pohead_vend_cntct_middle, 
                  pohead_vend_cntct_last_name, 
                  pohead_vend_cntct_suffix) AS vend_cntct, 
  pohead_vend_cntct_phone,
  formatCntctName(pohead_shipto_cntct_honorific, 
                  pohead_shipto_cntct_first_name, 
                  pohead_shipto_cntct_middle, 
                  pohead_shipto_cntct_last_name, 
                  pohead_shipto_cntct_suffix) AS shipto_cntct, 
  pohead_shipto_cntct_phone,
  min(poitem_duedate)  AS duedate, pohead_status AS status,
  CASE WHEN (pohead_status = ''O'') THEN <? value("open") ?>
  ELSE <? value("unreleased") ?> END AS status_qtdisplayrole,
  SUM(round(noNeg(poitem_qty_ordered - poitem_qty_received + poitem_qty_returned) *            
        currToBase(pohead_curr_id, poitem_unitprice, pohead_orderdate), 2) ) AS amount,
  CASE WHEN (min(poitem_duedate) <= current_date) THEN ''error'' 
  ELSE ''emphasis'' END AS duedate_qtforegroundrole,
  ''curr'' AS amount_xtnumericrole
FROM pohead 
  JOIN vendinfo ON (pohead_vend_id=vend_id) 
  JOIN poitem ON (poitem_pohead_id=pohead_id) 
WHERE (poitem_status IN (''U'',''O''))
GROUP BY pohead_id, pohead_number, pohead_status, vend_number, vend_name, pohead_shipvia, pohead_vend_cntct_id, 
  pohead_shipto_cntct_id, pohead_vend_cntct_phone, pohead_shipto_cntct_phone, pohead_vend_cntct_honorific,   
  pohead_vend_cntct_first_name,  pohead_vend_cntct_middle, pohead_vend_cntct_last_name, pohead_vend_cntct_suffix,
  pohead_shipto_cntct_honorific, pohead_shipto_cntct_first_name, pohead_shipto_cntct_middle, pohead_shipto_cntct_last_name, 
  pohead_shipto_cntct_suffix
ORDER BY pohead_number;	
', NULL, NULL, 0);
INSERT INTO pkgmetasql (metasql_id, metasql_group, metasql_name, metasql_notes, metasql_query, metasql_lastuser, metasql_lastupdate, metasql_grade) VALUES (222, 'desktop', 'salesAct', ' Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple. See www.xtuple.com/CPAL for the full text of the software license.', '-- Group: desktop
-- Name:  salesAct
-- Notes: 
-- Copyright (c) 1999-2014 by OpenMFG LLC, d/b/a xTuple.
-- See www.xtuple.com/CPAL for the full text of the software license.

SELECT ''Q'' AS activity, <? value("quotes") ?> AS activity_qtdisplayrole, 0 AS seq,
  COUNT(quhead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole   
FROM 
 (SELECT quhead_id,
    SUM(round((noNeg(quitem_qtyord) * quitem_qty_invuomratio) *            
        (currToBase(quhead_curr_id, quitem_price, quhead_quotedate) / quitem_price_invuomratio), 2) ) AS amount
     FROM quitem
       JOIN quhead ON (quitem_quhead_id=quhead_id)
     WHERE ( COALESCE(quhead_expire,current_date + 1) > current_date
       AND   quhead_status != ''C''  )
   GROUP BY quhead_id) data
HAVING (COUNT(quhead_id) > 0)
UNION 
SELECT ''O'' AS activity, <? value("open") ?> AS activity_qtdisplayrole,  1 AS seq,   
  COUNT(cohead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole
FROM
  (SELECT cohead_id,
    SUM(round((noNeg(coitem_qtyord - coitem_qtyshipped + coitem_qtyreturned) * coitem_qty_invuomratio) *            
        (currToBase(cohead_curr_id, coitem_price, cohead_orderdate) / coitem_price_invuomratio), 2) ) AS amount
     FROM coitem
       JOIN cohead ON (coitem_cohead_id=cohead_id)
     WHERE ( coitem_status = ''O'' )
   GROUP BY cohead_id) data
HAVING (COUNT(cohead_id) > 0)
UNION
SELECT ''P'' AS activity, <? value("print") ?> AS activity_qtdisplayrole, 2 AS seq,   
  COUNT(cohead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole
FROM
  (SELECT cohead_id,
    SUM(round((noNeg(coitem_qtyord - coitem_qtyshipped + coitem_qtyreturned) * coitem_qty_invuomratio) *            
        (currToBase(cohead_curr_id, coitem_price, cohead_orderdate) / coitem_price_invuomratio), 2) ) AS amount
     FROM coitem
       JOIN cohead ON (coitem_cohead_id=cohead_id)
       JOIN pack ON ((pack_head_type=''SO'')
                 AND (pack_head_id=cohead_id))
     WHERE ( (coitem_status = ''O'')
      AND (NOT pack_printed) )
   GROUP BY cohead_id) data
HAVING (COUNT(cohead_id) > 0)
UNION
SELECT ''S'' AS activity, <? value("ship") ?> AS activity_qtdisplayrole, 3 AS seq,   
  COUNT(cohead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole
FROM
  (SELECT cohead_id,
    SUM(round((shipitem_qty * coitem_qty_invuomratio) *            
        (currToBase(cohead_curr_id, coitem_price, cohead_orderdate) / coitem_price_invuomratio), 2) ) AS amount
     FROM coitem
       JOIN cohead ON (coitem_cohead_id=cohead_id)
       JOIN shipitem ON (shipitem_orderitem_id=coitem_id)
       JOIN shiphead ON ((shiphead_order_type=''SO'')
                    AND (shipitem_shiphead_id=shiphead_id))
     WHERE ( (coitem_status = ''O'')
      AND (NOT shiphead_shipped) )
   GROUP BY cohead_id) data
HAVING (COUNT(cohead_id) > 0)
UNION
SELECT ''B'' AS activity, <? value("bill") ?> AS activity_qtdisplayrole, 4 AS seq,   
  COUNT(cohead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole
FROM
  (SELECT cohead_id,
     CASE WHEN ( 
      -- For kits only when entire kit has shipped
      item_type != ''K'' OR NOT coitem_id IN (
        SELECT sub.coitem_id
          FROM coitem sub
         WHERE((sub.coitem_cohead_id=coitem_cohead_id)
           AND (sub.coitem_linenumber=coitem_linenumber)
           AND (sub.coitem_subnumber > 0)
           AND ((sub.coitem_qtyord - sub.coitem_qtyshipped + sub.coitem_qtyreturned) > 0))
      )) THEN
      -- Total shipped not invoiced minus any already selected for billing not invoiced
      SUM(round(((shipitem_qty -
      COALESCE((SELECT sum(cobill_qty) 
                FROM cobill 
                WHERE ((cobill_coitem_id=coitem_id 
                  AND (cobill_invcitem_id IS NULL)
                  ))),0) 
       * coitem_qty_invuomratio) *            
          (currToBase(cohead_curr_id, coitem_price, cohead_orderdate) / coitem_price_invuomratio)),2))
     ELSE 0
     END AS amount
     FROM coitem
       JOIN itemsite ON (coitem_itemsite_id=itemsite_id)
       JOIN item ON (itemsite_item_id=item_id)
       JOIN cohead ON (coitem_cohead_id=cohead_id)
       JOIN custinfo ON (cust_id=cohead_cust_id)
       JOIN shipitem ON (shipitem_orderitem_id=coitem_id)
       JOIN shiphead ON ((shiphead_order_type=''SO'')
                 AND (shiphead_id=shipitem_shiphead_id))
     WHERE ( (coitem_cohead_id=cohead_id)
      AND (coitem_status = ''O'')
      AND (shiphead_shipped)
      AND (NOT shipitem_invoiced)
      AND (shipitem_qty > COALESCE((SELECT sum(cobill_qty)
                                    FROM cobill
                                    WHERE cobill_coitem_id=coitem_id AND cobill_invcitem_id IS NULL), 0)) )
   GROUP BY cohead_id, coitem_id, coitem_qtyord, coitem_qtyshipped, coitem_qtyreturned, 
            cust_partialship, item_type) data
HAVING (COUNT(cohead_id) > 0)
UNION
SELECT ''I'' AS activity, <? value("invoice") ?> AS activity_qtdisplayrole, 5 AS seq,   
  COUNT(cohead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole
FROM
  (SELECT cohead_id,
    SUM(round((cobill_qty * coitem_qty_invuomratio) *            
        (currToBase(cohead_curr_id, coitem_price, cohead_orderdate) / coitem_price_invuomratio), 2) ) AS amount
     FROM coitem
       JOIN cohead ON (coitem_cohead_id=cohead_id)
       JOIN cobill ON (cobill_coitem_id=coitem_id)
     WHERE ( (coitem_status = ''O'')
      AND (cobill_invcitem_id IS NULL) )
   GROUP BY cohead_id) data
HAVING (COUNT(cohead_id) > 0)
UNION
SELECT ''T'' AS activity, <? value("post") ?> AS activity_qtdisplayrole, 5 AS seq,   
  COUNT(invchead_id) AS count, COALESCE(sum(amount),0) AS amount, ''curr'' AS amount_xtnumericrole
FROM
  (SELECT invchead_id,
    SUM(round((invcitem_billed * invcitem_qty_invuomratio) * (invcitem_price / COALESCE(invcitem_price_invuomratio,1)),2)
 ) AS amount
     FROM invcitem
       JOIN invchead ON (invcitem_invchead_id=invchead_id)
     WHERE ( NOT invchead_posted )
   GROUP BY invchead_id) data
HAVING (COUNT(invchead_id) > 0)
ORDER BY seq;
', NULL, NULL, 0);
