INSERT INTO pkgscript (script_id, script_name, script_order, script_enabled, script_source, script_notes) VALUES (77, 'dockMyTodo', 0, true, '/*
 * This file is part of the xTuple ERP: PostBooks Edition, a free and
 * open source Enterprise Resource Planning software suite,
