  var desktop = toolbox.loadUi(uiName);
  _desktopStack.addWidget(desktop);
  _windows[_windows.length] = desktop;
  addToolBarAction(desktop.windowTitle, imageName, privilege);
  desktop.restoreState();

  return desktop;
}

/*!
